import React from 'react'
import './Resume.css';
import SchoolIcon from '@mui/icons-material/School';
import { Grid , Icon, Paper, Typography} from "@material-ui/core";   
import resumeData from '../../utils/resumeData';
import  CustomTimeline , {CustomTimelineSeparator} from "../../components/Timeline/Timeline";
import WorkIcon from '@mui/icons-material/Work';

import { TimelineConnector, TimelineContent, TimelineItem, TimelineSeparator } from '@material-ui/lab';


const Resume = () => {
    return (
    <div>

{/* About me */}
        <Grid container className="section pb_45">
        <Grid item className='section_title mb_30'>
            <span></span>
            <h6 className="section_title-text">About me</h6>

        </Grid>
        <Grid item xs={12}>
            <Typography variant='body2'className='aboutme_text'>{resumeData.about}</Typography>

            </Grid>

        </Grid>



{/*Education and experiences */}
<Grid container className="section">
<Grid item className='section_title mb_30'>
            <span></span>
            <h6 className="section_title-text">Resume</h6>

            </Grid>
            </Grid>

        <Grid item xs={12}>
            <Grid container className='resume_timeline'>

                {/* Experiences*/}
                <Grid item sm={12} md={6}>
                    <CustomTimeline title='Work Experiences'   icon={<WorkIcon/>}>
                         {resumeData.experiences.map( (experience) => (
                             <TimelineItem>

                                 <CustomTimelineSeparator />

                                 <TimelineContent className='timline_content'>

                                     <Typography className='timeline_title'>{experience.title  }</Typography>
                                     <Typography variant='caption' className='timline_date'>{experience.date }</Typography>
                                    <Typography variant='body2' className='timeline_ description'>{experience. description}</Typography>
                                 </TimelineContent>
                             </TimelineItem>
                         )
                            )}

                    </CustomTimeline>
                </Grid>

                       {/* Education */}
                       <Grid item sm={12} md={6}>
                    <CustomTimeline title='Education History'   icon={<SchoolIcon/>}>
                         {resumeData.educations.map( (education) => (
                             <TimelineItem>

                                 <CustomTimelineSeparator />

                                 <TimelineContent className='timline_content'>

                                     <Typography className='timeline_title'>{education.title  }</Typography>
                                     <Typography variant='caption' className='timline_date'>{education.date }</Typography>
                                    <Typography variant='body2' className='timeline_ description'>{education.description}</Typography>
                                 </TimelineContent>
                             </TimelineItem>
                         )
                            )}

                    </CustomTimeline>
                </Grid>


                </Grid>
                </Grid>
                {/*Services  */}
<Grid container className="section pb_45" >
<Grid item className='section_title mb_30'>
            <span></span>
            <h6 className="section_title-text">Services</h6>
        </Grid>
        <Grid item xs={12}>
            <Grid Container  spacing={3} justify='space-around' style={{display:"flex"}}>
                {resumeData.services.map((service) =>(
                    <Grid item xs={12} sm={6} md={3}> 
                    <div className='service'>
                        <Icon className='service_icon'>{ServiceWorker.icon}</Icon>
                        <Typography className='service_title' variant='h6' >{service.title}</Typography>
                        <Typography className='service_description' variant='body2' >{service.description}</Typography>
                    </div>
                    </Grid>
                ))}
            </Grid>
        </Grid>

</Grid>

     {/* Skills */}    
     <Grid 
     container 
      spacing={3} 
     justify='space-between' 
     className="section graybg pb_45" >
         {resumeData.skills.map((skill) => (
             <Grid item xs={12} sm={6} md={3}> 
             <Paper elevation={0}>
                 <Typography variant='h6' className="skills_title">
                     {skill.title}

                 </Typography>
                    {skill.description.map((element) => ( 
                        <Typography>
                            <TimelineDot>
                                {element}
                            </TimelineDot>
                        </Typography>
                     ))}

             </Paper>

             </Grid>
         ))}
         </Grid>   


   </div>

    );
};

export default Resume;
